// These code snippets use an open-source library. http://unirest.io/php

/**
 * @var Unirest\Response $response
 */
$response = Unirest\Request::post("https://instapi.p.mashape.com/me/getUploads.json",
    array(
        "X-Mashape-Key" => $this->getData('mashapekey', $contentId),

    ),
    array(
        "login" => $this->getData('username', $contentId),
        "token" => $this->getData('password', $contentId)
    )
);


$data = json_decode($response->raw_body, true);

$items = $data['data']['response']['response'][0]['items'];

$length = count($items);

$length = $length >= 8 ? 7 : $length;

echo '<section class="instagram_galery">';

$username = $items[0]['user']['username'] ?: $this->getData('username', $contentId);
if (substr($username, -1) != 's') {
    $username = $username .'\'s';
} else {
    $username = $username . '\'';
}

$profile_image = $items[0]['user']['profile_pic_url'] ?: 'data:image/png;base64,';

echo '<header>';

echo '<img class="instagram_gallery_profile" src="'.$profile_image.'">';

echo '<h1>'.$username.' Galerie</h1>';
echo '</header>';
for ($i = 0; $i <= $length; $i++) {

    echo '<img class="instagram_gallery_image" src="'.$items[$i]['image_versions2']['candidates'][0]['url'].'">';


}

echo '</section>';



?>


